        <h2>Share your video with us: </h2>
        
        <el-form ref="form" :model="form" label-width="80px">
            <el-form-item label="标题">
                <el-input v-model="form.title"></el-input>
            </el-form-item>
            <el-form-item label="视频地址">
                <el-input type="url" v-model="form.url"></el-input>
            </el-form-item>
            <el-form-item label="描述">
                <el-input type="textarea" v-model="form.info"></el-input>
            </el-form-item>

            <el-form-item label="视频封面">
                <el-upload
                class="avatar-uploader"
                label="描述"
                action=""
                ref="upload"
                :before-upload="fnBeforeUpload"
                :http-request="fnUploadRequest"
                :show-file-list="false">
                <img v-if="imageUrl" :src="imageUrl" class="avatar">
                <i v-else class="el-icon-plus avatar-uploader-icon"></i>
                <div class="el-upload__tip" slot="tip">只能上传图片文件，且不能超过2M</div>
                </el-upload>
            </el-form-item>

            <el-form-item>
                <el-button type="primary" icon="el-icon-edit" circle @click="onSubmit">Upload</el-button>
            </el-form-item>
        </el-form>
    </div>
</template>
<script>
import * as API from '@/api/video/';
import uplpadAPI from '@/api/upload/';
export default {
    name: 'PostVideo',
    data() {
        return {
            imageUrl: '',
            dialogImageUrl: '',
            dialogVisible: false,
            form: {
                title: '',
                info: '',
                url: '',
                avatar: '',
            }
        }
    },
    methods: {
        fnBeforeUpload(file) {
            const isImage = (file.type === 'image/png' || file.type === 'image/jpeg');
            const isLt2M = file.size / 1024 / 1024 < 2;
            if (!isImage) {
                this.$message.error('上传头像图片只能是图片!');
            }
            if (!isLt2M) {
                this.$message.error('上传头像图片大小不能超过 2MB!');
            }
            return isImage && isLt2M;
            },
            //改掉默认上传方式
        fnUploadRequest(option) {
            uplpadAPI(option.file.name).then((res) => {
                const oReq = new XMLHttpRequest();
                oReq.open('PUT', res.data.put, true);
                oReq.send(option.file);
                oReq.onload = () => {
                    this.imageUrl = res.data.get;
                    this.form.avatar = res.data.key;
                };
            }).catch((error) => {
                this.$notify.error({
                title: '网路错误，或者服务器宕机',
                message: error,
                });
            });
        },
        onSubmit(){
            API.postVideo(this.form).then(res=>{
                if(res.status>0){//判断状态码
                    this.$notify({
                    title: 'Submit Failed!',
                    message: res.msg,
                    type: 'error',
                });
                }else {
                    this.$notify({
                        title: 'Submit success!',
                        message: `your posted video's id: ${res.data.id}`,
                        type: 'success',
                    });
                    this.$router.push({path: '/'});
                }
            }).catch(err => {
                this.$notify({
                    title: 'network err or golang server internal error!!!',
                    message: err,
                    type: 'error',
                });
            });
        }
    },
}
</script>
<style lang="scss" scoped>
  .avatar-uploader .el-upload {
    border: 1px dashed #d9d9d9;
    border-radius: 6px;
    cursor: pointer;
    position: relative;
    overflow: hidden;
  }
  .avatar-uploader .el-upload:hover {
    border-color: #409EFF;
  }
  .avatar-uploader-icon {
    font-size: 28px;
    color: #8c939d;
    width: 178px;
    height: 178px;
    line-height: 178px;
    text-align: center;
  }
  .avatar {
    width: 178px;
    height: 178px;
    display: block;
  }
</style>